<?phpfunction bacola_add_elementor_page_settings_controls( $page ) {	$page->add_control( 'bacola_elementor_hide_page_header',		[			'label'          => esc_html__( 'Hide Header', 'bacola' ),            'type'           => \Elementor\Controls_Manager::SWITCHER,			'label_on'       => esc_html__( 'Yes', 'bacola' ),			'label_off'      => esc_html__( 'No', 'bacola' ),			'return_value'   => 'yes',			'default'        => 'no',		]	);		$page->add_control( 'bacola_elementor_hide_page_footer',		[			'label'          => esc_html__( 'Hide Footer', 'bacola-core' ),			'type'           => \Elementor\Controls_Manager::SWITCHER,			'label_on'       => esc_html__( 'Yes', 'bacola' ),			'label_off'      => esc_html__( 'No', 'bacola' ),			'return_value'   => 'yes',			'default'        => 'no',		]	);}add_action( 'elementor/element/wp-page/document_settings/before_section_end', 'bacola_add_elementor_page_settings_controls' );